const xlsxServices = form.getXlsxServices();

const sheetId = xlsxServices.getSheetIdByName("Simple");
const rowCount = xlsxServices.getRowCount(sheetId);
const columnCount = xlsxServices.getColumnCount(sheetId, 0);

for (let columnIndex = columnCount - 1; columnIndex >= 0; columnIndex--) {
  const cellSource = xlsxServices.getCellSource(sheetId, columnIndex, 0);
  const cellField = cellSource ? cellSource.getField() : null;
  const fieldType = cellField ? String(cellField.getType()) : null;

  if (fieldType === "table") {
    xlsxServices.insertColumnAfter(sheetId, columnIndex);
    columnCount++;
  }
}

for (let columnIndex = columnCount - 1; columnIndex >= 0; columnIndex--) {
  for (let rowIndex = 0; rowIndex < rowCount; rowIndex++) {
    const cell = xlsxServices.getCell(sheetId, rowIndex, columnIndex);
    const cellSource = xlsxServices.getCellSource(sheetId, rowIndex, columnIndex);

    if (cell && cellSource) {
      const cellSourceType = String(cellSource.getType());
      const cellField = cellSource.getField();
      const fieldType = String(cellField.getType());

      if (fieldType == "table") {
        switch (cellSourceType) {
          case "HEADER":                    
            xlsxServices.setCellValueString(sheetId, 0, columnIndex + 1, "Sum field");
          break;
          case "VALUE":
            const link = cell.getHyperlink();
            if (link) {
              const tableSheetName = link.getAddress();
              const tableSheetId = xlsxServices.getSheetIdByName(tableSheetName);
              const tableRows = xlsxServices.getRowCount(tableSheetId);
              const formula = `SUM('${tableSheetName}'!B1:'${tableSheetName}'!B${tableRows - 1})`;
              xlsxServices.setCellFormula(sheetId, rowIndex, columnIndex + 1, formula);
            }
          break;
        }
      }
    }
  }
}
